import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './myplan.css';
import logo from '../images/beefit-logo.png';

// Modal Component for Selecting Exercises
const ExerciseModal: React.FC<{
    exercises: Exercise[];
    onClose: () => void;
    onSelectExercise: (exercise: Exercise) => void;
}> = ({ exercises, onClose, onSelectExercise }) => {
    const [selectedExercise, setSelectedExercise] = useState<Exercise | null>(null);

    return (
        <div className="modal">
            <div className="modal-content">
                <h2>Select Exercise</h2>
                {exercises.map((exercise) => (
                    <div key={exercise.id} className="modal-exercise-item">
                        <input
                            type="radio"
                            id={exercise.title}
                            name="exercise"
                            value={exercise.title}
                            onChange={() => setSelectedExercise(exercise)}
                        />
                        <label htmlFor={exercise.title}>
                            {exercise.title} - {exercise.kcal} KCAL - {exercise.mins} MINS
                        </label>
                    </div>
                ))}
                {/* Display GIF of the selected exercise */}
                {selectedExercise && (
                    <div className="exercise-gif-container">
                        <img src={selectedExercise.gif} alt={selectedExercise.title} />
                    </div>
                )}
                <div className="modal-actions">
                    <button
                        disabled={!selectedExercise}
                        onClick={() => {
                            if (selectedExercise) {
                                onSelectExercise(selectedExercise);
                            }
                            onClose();
                        }}
                    >
                        Done
                    </button>
                    <button onClick={onClose}>Cancel</button>
                </div>
            </div>
        </div>
    );
};

interface Exercise {
    id: string;
    title: string;
    kcal: number;
    mins: number;
    gif: string; // Added GIF property
}

const MyPlan: React.FC = () => {
    const [totalExercises, setTotalExercises] = useState<number>(0);
    const [totalKcal, setTotalKcal] = useState<number>(0);
    const [totalMins, setTotalMins] = useState<number>(0);
    const [selectedExercises, setSelectedExercises] = useState<Exercise[]>([]);
    const [showModal, setShowModal] = useState<boolean>(false);
    const [activeCategory, setActiveCategory] = useState<string | null>(null);
    const [categoryExercises, setCategoryExercises] = useState<Exercise[]>([]);
    const [showLevels, setShowLevels] = useState<boolean>(false);

    // Workout data for different difficulty levels with GIF URLs
    const workoutData: Record<string, Record<string, Exercise[]>> = {
        fullBody: {
            beginner: [
                { id: 'fullBody1', title: 'Push Ups', kcal: 50, mins: 10, gif: 'https://media3.giphy.com/media/v1.Y2lkPTc5MGI3NjExZzhoMWNnbjFnOTE3c2pwZWw0bXZ3cGt5dDdvcmJ0N3lxMDltcDFhMiZlcD12MV9pbnRlcm5hbF9naWZfYnlfaWQmY3Q9Zw/xTiTnlS1f0DlwzCkko/giphy.webp' },
                { id: 'fullBody2', title: 'Squats', kcal: 100, mins: 15, gif: 'https://media2.giphy.com/media/v1.Y2lkPTc5MGI3NjExcTZ3Zm0xM3Z5Nmgxdmh3ejg2NG84YmRlb3duOXRxY2Uwdm9zOWdsciZlcD12MV9naWZzX3NlYXJjaCZjdD1n/HIlZpE5TxLHVmfTTxe/giphy.webp' },
            ],
            intermediate: [
                { id: 'fullBody3', title: 'Burpees', kcal: 80, mins: 10, gif: 'https://i.makeagif.com/media/1-27-2017/iKECQ7.gif' },
                { id: 'fullBody4', title: 'Lunges', kcal: 90, mins: 12, gif: 'https://media.tenor.com/fWiC9Ze5eUMAAAAM/lunges-exercise.gif' },
            ],
            advanced: [
                { id: 'fullBody5', title: 'Mountain Climbers', kcal: 100, mins: 15, gif: 'https://www.mitrecsports.com/assets/Mountain-Climbers-Gif.gif' },
                { id: 'fullBody6', title: 'Deadlifts', kcal: 150, mins: 20, gif: 'https://www.journalmenu.com/wp-content/uploads/2018/03/deadlift-gif-side.gif' },
            ],
        },
        abs: {
            beginner: [
                { id: 'abs1', title: 'Planks', kcal: 30, mins: 5, gif: 'https://media.tenor.com/6SOetkNbfakAAAAM/plank-abs.gif' },
                { id: 'abs2', title: 'Crunches', kcal: 50, mins: 10, gif: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcS-HyWOI9XzwnOgJW3B4s2BPpI6diwr38stQQ&s' },
            ],
            intermediate: [
                { id: 'abs3', title: 'Leg Raises', kcal: 40, mins: 8, gif: 'url_to_leg_raise_gif.gif' },
                { id: 'abs4', title: 'Russian Twists', kcal: 60, mins: 12, gif: 'url_to_russian_twist_gif.gif' },
            ],
            advanced: [
                { id: 'abs5', title: 'Bicycle Crunches', kcal: 70, mins: 10, gif: 'url_to_bicycle_crunch_gif.gif' },
                { id: 'abs6', title: 'V-Ups', kcal: 90, mins: 15, gif: 'url_to_vup_gif.gif' },
            ],
        },
        arm: {
            beginner: [
                { id: 'arm1', title: 'Bicep Curls', kcal: 40, mins: 10, gif: 'url_to_bicep_curl_gif.gif' },
                { id: 'arm2', title: 'Tricep Dips', kcal: 60, mins: 15, gif: 'url_to_tricep_dip_gif.gif' },
            ],
            intermediate: [
                { id: 'arm3', title: 'Hammer Curls', kcal: 50, mins: 12, gif: 'url_to_hammer_curl_gif.gif' },
                { id: 'arm4', title: 'Overhead Press', kcal: 70, mins: 14, gif: 'url_to_overhead_press_gif.gif' },
            ],
            advanced: [
                { id: 'arm5', title: 'Arnold Press', kcal: 80, mins: 15, gif: 'url_to_arnold_press_gif.gif' },
                { id: 'arm6', title: 'Pull Ups', kcal: 100, mins: 20, gif: 'url_to_pullup_gif.gif' },
            ],
        },
    };

    const handleCategoryClick = (category: string) => {
        setActiveCategory(category);
        setShowLevels(true); // Show level options when category is clicked
    };

    const handleLevelSelect = (level: string) => {
        if (activeCategory) {
            setCategoryExercises(workoutData[activeCategory][level]);
            setShowModal(true); // Show exercises after level is selected
        }
        setShowLevels(false); // Hide the level options after selection
    };

    const handleSelectExercise = (exercise: Exercise) => {
        setTotalExercises(totalExercises + 1);
        setTotalKcal(totalKcal + exercise.kcal);
        setTotalMins(totalMins + exercise.mins);
        setSelectedExercises((prevExercises) => [...prevExercises, exercise]);
    };

    const handleClearExercise = (exerciseId: string) => {
        const updatedExercises = selectedExercises.filter(exercise => exercise.id !== exerciseId);
        const clearedExercise = selectedExercises.find(exercise => exercise.id === exerciseId);

        if (clearedExercise) {
            setTotalExercises(totalExercises - 1);
            setTotalKcal(totalKcal - clearedExercise.kcal);
            setTotalMins(totalMins - clearedExercise.mins);
        }

        setSelectedExercises(updatedExercises);
    };

    const closeModal = () => {
        setShowModal(false);
        setActiveCategory(null);
    };

    return (
        <div className="myplan-container">
            <div className="myplan-content">
                <img src={logo} alt="Beefit Logo" className="logo" />
                <h1 className="page-title">WORKOUT PLANS</h1>

                <div className="stats">
                    <div className="stat">
                        <h2>{totalExercises}</h2>
                        <p>EXERCISES</p>
                    </div>
                    <div className="stat">
                        <h2>{totalKcal}</h2>
                        <p>KCAL</p>
                    </div>
                    <div className="stat">
                        <h2>{totalMins}</h2>
                        <p>MINS</p>
                    </div>
                </div>

                <p className="choose-workout">Choose your Workout!</p>

                <div className="workout-grid">
                    <div className="workout-card" onClick={() => handleCategoryClick('fullBody')}>
                        <img src="./images/full-body.webp" alt="Full Body" className="workout-image" />
                        <p className="workout-title">FULL BODY WORKOUT</p>
                    </div>
                    <div className="workout-card" onClick={() => handleCategoryClick('abs')}>
                        <img src="./images/abs-beginner.jpg" alt="Abs Beginner" className="workout-image" />
                        <p className="workout-title">ABS BEGINNER</p>
                    </div>
                    <div className="workout-card" onClick={() => handleCategoryClick('arm')}>
                        <img src="./images/arm.jpg" alt="Arm" className="workout-image" />
                        <p className="workout-title">ARM WORKOUT</p>
                    </div>
                </div>

                {/* Display Level Options */}
                {showLevels && activeCategory && (
                    <div className="level-options">
                        <button onClick={() => handleLevelSelect('beginner')}>Beginner</button>
                        <button onClick={() => handleLevelSelect('intermediate')}>Intermediate</button>
                        <button onClick={() => handleLevelSelect('advanced')}>Advanced</button>
                    </div>
                )}

                {/* Display Selected Exercises */}
                <div className="selected-exercises">
                    <h2>Selected Exercises</h2>
                    {selectedExercises.map(exercise => (
                        <div key={exercise.id} className="selected-exercise-item">
                            <span>{exercise.title}</span>
                            <button onClick={() => handleClearExercise(exercise.id)}>Remove</button>
                        </div>
                    ))}
                </div>

                {/* Modal for Exercise Selection */}
                {showModal && (
                    <ExerciseModal
                        exercises={categoryExercises}
                        onClose={closeModal}
                        onSelectExercise={handleSelectExercise}
                    />
                )}
            </div>
        </div>
    );
};

export default MyPlan;
