import React, { useState } from 'react';
import './myplan.css';

const MyPlan = () => {
    // State for tracking total exercises, calories, and minutes
    const [totalExercises, setTotalExercises] = useState(0);
    const [totalKcal, setTotalKcal] = useState(0);
    const [totalMins, setTotalMins] = useState(0);
    const [selectedExercises, setSelectedExercises] = useState([]);
    const [selectedWorkout, setSelectedWorkout] = useState(null);
    const [subExercises, setSubExercises] = useState([]);

    const workoutData = {
        fullBody: {
            exercises: [
                { id: 1, title: 'Push Ups', kcal: 50, mins: 10 },
                { id: 2, title: 'Squats', kcal: 100, mins: 15 },
            ],
        },
        abs: {
            exercises: [
                { id: 1, title: 'Planks', kcal: 30, mins: 5 },
                { id: 2, title: 'Crunches', kcal: 50, mins: 10 },
            ],
        },
        arm: {
            exercises: [
                { id: 1, title: 'Bicep Curls', kcal: 40, mins: 10 },
                { id: 2, title: 'Tricep Dips', kcal: 60, mins: 15 },
            ],
        },
    };

    const handleWorkoutClick = (workoutType) => {
        setSelectedWorkout(workoutType);
        setSubExercises(workoutData[workoutType].exercises);
    };

    const handleExerciseClick = (exercise) => {
        setTotalExercises(totalExercises + 1);
        setTotalKcal(totalKcal + exercise.kcal);
        setTotalMins(totalMins + exercise.mins);
        setSelectedExercises([...selectedExercises, exercise]);
    };

    const handleRemoveExercise = (exercise) => {
        // Remove exercise from selected exercises
        setTotalExercises(totalExercises - 1);
        setTotalKcal(totalKcal - exercise.kcal);
        setTotalMins(totalMins - exercise.mins);
        setSelectedExercises(selectedExercises.filter(item => item.id !== exercise.id));

        // If all exercises from the selected workout are removed, reset workout
        if (selectedExercises.length === 1) {
            setSelectedWorkout(null);
            setSubExercises([]);
        }
    };

    const handleEditExercise = (exercise, newKcal, newMins) => {
        const updatedExercises = selectedExercises.map(item => {
            if (item.id === exercise.id) {
                const kcalDiff = newKcal - item.kcal;
                const minsDiff = newMins - item.mins;
                setTotalKcal(totalKcal + kcalDiff);
                setTotalMins(totalMins + minsDiff);
                return { ...item, kcal: newKcal, mins: newMins };
            }
            return item;
        });
        setSelectedExercises(updatedExercises);
    };

    const handleRemoveWorkout = () => {
        // Reset totals and selected exercises when removing a workout
        selectedExercises.forEach(exercise => {
            setTotalExercises(totalExercises - 1);
            setTotalKcal(totalKcal - exercise.kcal);
            setTotalMins(totalMins - exercise.mins);
        });
        setSelectedExercises([]);
        setSelectedWorkout(null);
        setSubExercises([]);
    };

    return (
        <div className="myplan-container">
            <div className="myplan-content">
                <h1 className="page-title">WORKOUT PLANS</h1>

                <div className="stats">
                    <div className="stat">
                        <h2>{totalExercises}</h2>
                        <p>EXERCISES</p>
                    </div>
                    <div className="stat">
                        <h2>{totalKcal}</h2>
                        <p>KCAL</p>
                    </div>
                    <div className="stat">
                        <h2>{totalMins}</h2>
                        <p>MINS</p>
                    </div>
                </div>

                <p className="choose-workout">Choose your Workout!</p>

                <button className="create-plan-btn">CREATE YOUR PLAN HERE!</button>

                <div className="workout-grid">
                    <div className="workout-card" onClick={() => handleWorkoutClick('fullBody')}>
                        <img src="/images/full-body.webp" alt="Full Body" className="workout-image" />
                        <p className="workout-title">FULL BODY WORKOUT</p>
                    </div>

                    <div className="workout-card" onClick={() => handleWorkoutClick('abs')}>
                        <img src="/images/abs-beginner.jpg" alt="Abs Beginner" className="workout-image" />
                        <p className="workout-title">ABS BEGINNER</p>
                    </div>

                    <div className="workout-card" onClick={() => handleWorkoutClick('arm')}>
                        <img src="/images/arm-beginner.webp" alt="Arm Beginner" className="workout-image" />
                        <p className="workout-title">ARM BEGINNER</p>
                    </div>
                </div>

                {/* Display Subcategory Exercises */}
                {selectedWorkout && (
                    <div className="sub-exercises">
                        <h2>{selectedWorkout.toUpperCase()} SUB EXERCISES</h2>
                        <ul>
                            {subExercises.map((exercise) => (
                                <li key={exercise.id} className="sub-exercise">
                                    <span>{exercise.title} - {exercise.kcal} KCAL - {exercise.mins} MINS</span>
                                    <button onClick={() => handleExerciseClick(exercise)}>Add Exercise</button>
                                </li>
                            ))}
                        </ul>
                    </div>
                )}

                {/* Selected Exercises Section */}
                <div className="selected-exercises">
                    <h2>Selected Exercises</h2>
                    {selectedExercises.length > 0 ? (
                        <ul>
                            {selectedExercises.map((exercise) => (
                                <li key={exercise.id} className="selected-exercise">
                                    <span>{exercise.title} - {exercise.kcal} KCAL - {exercise.mins} MINS</span>
                                    <button className="edit-btn" onClick={() => handleEditExercise(exercise, prompt('Edit KCAL:', exercise.kcal), prompt('Edit MINS:', exercise.mins))}>Edit</button>
                                    <button className="remove-btn" onClick={() => handleRemoveExercise(exercise)}>Remove</button>
                                </li>
                            ))}
                        </ul>
                    ) : (
                        <p>No exercises selected yet.</p>
                    )}
                </div>

                {/* Remove Workout Button */}
                {selectedWorkout && (
                    <button className="remove-workout-btn" onClick={handleRemoveWorkout}>
                        Remove {selectedWorkout.toUpperCase()} Workout
                    </button>
                )}
            </div>
        </div>
    );
};

export default MyPlan;
