import React, { useState } from 'react';
import './myplan.css';
import logo from '../images/beefit-logo.png';
import texture from '../images/texture.png'; 

const MyPlan = () => {
    const [totalExercises, setTotalExercises] = useState(0);
    const [totalKcal, setTotalKcal] = useState(0);
    const [totalMins, setTotalMins] = useState(0);
    const [selectedExercises, setSelectedExercises] = useState([]);
    const [subExercises, setSubExercises] = useState([]);
    const [activeCategory, setActiveCategory] = useState(null);
    const [zoom, setZoom] = useState(false); // State to manage zoom effect
    const [clickedButton, setClickedButton] = useState(null); // Track which button is clicked

    const workoutData = {
        fullBody: [
            { id: 1, title: 'Push Ups', kcal: 50, mins: 10 },
            { id: 2, title: 'Squats', kcal: 100, mins: 15 },
            { id: 3, title: 'Burpees', kcal: 80, mins: 10 },
        ],
        abs: [
            { id: 1, title: 'Planks', kcal: 30, mins: 5 },
            { id: 2, title: 'Crunches', kcal: 50, mins: 10 },
            { id: 3, title: 'Leg Raises', kcal: 40, mins: 8 },
        ],
        arm: [
            { id: 1, title: 'Bicep Curls', kcal: 40, mins: 10 },
            { id: 2, title: 'Tricep Dips', kcal: 60, mins: 15 },
            { id: 3, title: 'Push Ups', kcal: 50, mins: 10 },
        ],
    };

    const handleCategoryClick = (category) => {
        setActiveCategory(category);
        setSubExercises(workoutData[category]);
    };

    const handleExerciseClick = (exercise) => {
        setTotalExercises(totalExercises + 1);
        setTotalKcal(totalKcal + exercise.kcal);
        setTotalMins(totalMins + exercise.mins);
        setSelectedExercises([...selectedExercises, exercise]);
    };

    const handleRemoveExercise = (exercise) => {
        setTotalExercises(totalExercises - 1);
        setTotalKcal(totalKcal - exercise.kcal);
        setTotalMins(totalMins - exercise.mins);
        setSelectedExercises(selectedExercises.filter(item => item.id !== exercise.id));
    };

    const handleEditExercise = (exercise, newKcal, newMins) => {
        const updatedExercises = selectedExercises.map(item => {
            if (item.id === exercise.id) {
                const kcalDiff = newKcal - item.kcal;
                const minsDiff = newMins - item.mins;
                setTotalKcal(totalKcal + kcalDiff);
                setTotalMins(totalMins + minsDiff);
                return { ...item, kcal: newKcal, mins: newMins };
            }
            return item;
        });
        setSelectedExercises(updatedExercises);
    };

    const handleButtonClick = (event) => {
        const button = event.target;
        setClickedButton(button);

        // Remove animation class after a short delay to allow re-triggering the animation
        setTimeout(() => {
            setClickedButton(null);
        }, 500); // Duration of the animation
    };

    return (
        <div className="myplan-container">
            <img src={logo} alt="Beefit Logo" className="logo" />
            <div className="myplan-content">
                <h1 className="page-title">My Plan</h1>

                <div className="stats">
                    <div className="stat">
                        <h3>Total Exercises</h3>
                        <p>{totalExercises}</p>
                    </div>
                    <div className="stat">
                        <h3>Total Calories Burned</h3>
                        <p>{totalKcal} kcal</p>
                    </div>
                    <div className="stat">
                        <h3>Total Minutes</h3>
                        <p>{totalMins} mins</p>
                    </div>
                </div>

                <button
                    className={`create-plan-btn ${clickedButton === 'create' ? 'button-click-animate' : ''}`}
                    onClick={(event) => handleButtonClick(event, 'create')}
                >
                    Create Plan
                </button>

                <div className="workout-grid">
                    {subExercises.map((exercise) => (
                        <div
                            key={exercise.id}
                            className="workout-card"
                            onClick={() => handleExerciseClick(exercise)}
                        >
                            <h3>{exercise.title}</h3>
                            <p>{exercise.kcal} kcal, {exercise.mins} mins</p>
                        </div>
                    ))}
                </div>

                <div className="selected-exercises">
                    {selectedExercises.map((exercise) => (
                        <div key={exercise.id} className="selected-exercise">
                            <p>{exercise.title}</p>
                            <button
                                className={`remove-btn ${clickedButton === 'remove' ? 'button-click-animate' : ''}`}
                                onClick={(event) => {
                                    handleButtonClick(event, 'remove');
                                    handleRemoveExercise(exercise);
                                }}
                            >
                                Remove
                            </button>
                        </div>
                    ))}
                </div>
            </div>
        </div>
    );
};

export default MyPlan;
