// Weightloss.tsx
import React from 'react';
import { useNavigate } from 'react-router-dom';
import './Weightloss.css'; // Add styles specific to this component

const Weightloss: React.FC = () => {
    const navigate = useNavigate();

    return (
        <div className="weight-loss-page">
            <h1>Weight Loss Plan & Suggestions</h1>
            <p>Welcome to your weight loss journey! Here are some key strategies to help you achieve your goals:</p>

            <section className="tips-section">
                <h2>Weight Loss Tips</h2>
                <ul>
                    <li><strong>Caloric Deficit:</strong> Reduce your calorie intake and focus on nutrient-dense foods.</li>
                    <li><strong>Exercise Regularly:</strong> Incorporate cardio and strength training into your routine.</li>
                    <li><strong>Stay Hydrated:</strong> Drinking water helps boost metabolism and reduce hunger.</li>
                    <li><strong>Get Enough Sleep:</strong> Aim for 7-9 hours of quality sleep to support recovery and fat loss.</li>
                </ul>
            </section>

            <section className="sample-meal-plan">
                <h2>Sample Meal Plan</h2>
                <ul>
                    <li><strong>Breakfast:</strong> Scrambled eggs with spinach and whole grain toast.</li>
                    <li><strong>Lunch:</strong> Grilled chicken salad with olive oil and vinegar dressing.</li>
                    <li><strong>Snack:</strong> Greek yogurt with mixed berries.</li>
                    <li><strong>Dinner:</strong> Baked salmon with roasted vegetables and quinoa.</li>
                </ul>
            </section>

            <button className="back-btn" onClick={() => navigate('/logfitness')}>
                Back to Log Fitness
            </button>
        </div>
    );
};

export default Weightloss;
