import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './ScheduleWorkout.css'; // Add relevant CSS
import logo from '../images/beefit-logo.png';

interface Exercise {
  id: string;
  title: string;
  kcal: number;
  mins: number;
  gif: string;
}

interface Meal {
  id: string;
  title: string;
  kcal: number;
}

const ScheduleWorkout: React.FC = () => {
  const [selectedExercises, setSelectedExercises] = useState<Exercise[]>([]);
  const [selectedMeals, setSelectedMeals] = useState<Meal[]>([]);
  const [totalKcal, setTotalKcal] = useState<number>(0);
  const [totalMins, setTotalMins] = useState<number>(0);
  const [totalMealKcal, setTotalMealKcal] = useState<number>(0);
  const [workoutType, setWorkoutType] = useState<string>('fullBody'); // Default to full body
  const [showSchedule, setShowSchedule] = useState<boolean>(false);

  const workoutData: Record<string, Exercise[]> = {
    fullBody: [
      { id: '1', title: 'Push Ups', kcal: 50, mins: 10, gif: 'https://media.post.rvohealth.io/wp-content/uploads/sites/2/2019/05/PERFECT-PUSHUP.gif' },
      { id: '2', title: 'Squats', kcal: 100, mins: 15, gif: 'https://media2.giphy.com/media/v1.Y2lkPTc5MGI3NjExcTZ3Zm0xM3Z5Nmgxdmh3ejg2NG84YmRlb3duOXRxY2Uwdm9zOWdsciZlcD12MV9naWZzX3NlYXJjaCZjdD1n/HIlZpE5TxLHVmfTTxe/giphy.webp' },
    ],
    abs: [
      { id: '3', title: 'Planks', kcal: 30, mins: 5, gif: 'https://media.tenor.com/6SOetkNbfakAAAAM/plank-abs.gif' },
      { id: '4', title: 'Crunches', kcal: 50, mins: 10, gif: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcS-HyWOI9XzwnOgJW3B4s2BPpI6diwr38stQQ&s' },
    ],
    arms: [
      { id: '5', title: 'Bicep Curls', kcal: 40, mins: 10, gif: 'https://media1.popsugar-assets.com/files/thumbor/LvmfizMv8KR6r7MkMR-TmOrs9ck/fit-in/2048xorig/filters:format_auto-!!-:strip_icc-!!-/2021/08/27/563/n/1922729/a776b229bf1e8e99_IMB_MUp4Uh/i/Bicep-Curl.GIF' },
      { id: '6', title: 'Tricep Dips', kcal: 60, mins: 15, gif: 'https://i0.wp.com/post.healthline.com/wp-content/uploads/2019/05/Chair-dip.gif?w=1155&h=840' },
    ],
  };

  const mealData: Meal[] = [
    { id: '1', title: 'Oatmeal', kcal: 150 },
    { id: '2', title: 'Grilled Chicken', kcal: 300 },
    { id: '3', title: 'Salad', kcal: 100 },
    { id: '4', title: 'Protein Shake', kcal: 200 },
  ];

  const handleExerciseSelect = (exercise: Exercise) => {
    setSelectedExercises((prev) => [...prev, exercise]);
    setTotalKcal((prev) => prev + exercise.kcal);
    setTotalMins((prev) => prev + exercise.mins);
  };

  const handleExerciseRemove = (exercise: Exercise) => {
    setSelectedExercises((prev) => prev.filter(e => e.id !== exercise.id));
    setTotalKcal((prev) => prev - exercise.kcal);
    setTotalMins((prev) => prev - exercise.mins);
  };

  const handleMealSelect = (meal: Meal) => {
    setSelectedMeals((prev) => [...prev, meal]);
    setTotalMealKcal((prev) => prev + meal.kcal);
  };

  const handleMealRemove = (meal: Meal) => {
    setSelectedMeals((prev) => prev.filter(m => m.id !== meal.id));
    setTotalMealKcal((prev) => prev - meal.kcal);
  };

  const handleSchedule = () => {
    setShowSchedule(true);
  };

  return (
    <div className="schedule-workout-container">
      <img src={logo} alt="Beefit Logo" className="logo" />
      <h1>Schedule Your Workout and Diet</h1>
      <div className="workout-type-selection">
        <h2>Select Workout Type:</h2>
        <div className="buttons">
          <button onClick={() => setWorkoutType('fullBody')}>Full Body</button>
          <button onClick={() => setWorkoutType('abs')}>Abs</button>
          <button onClick={() => setWorkoutType('arms')}>Arms</button>
        </div>
      </div>

      <div className="exercise-selection">
        <h2>{workoutType.charAt(0).toUpperCase() + workoutType.slice(1)} Exercises:</h2>
        <div className="exercise-list">
          {workoutData[workoutType].map((exercise) => (
            <div key={exercise.id} className="exercise-card">
              <h3>{exercise.title}</h3>
              <img src={exercise.gif} alt={exercise.title} />
              <p>{exercise.kcal} KCAL - {exercise.mins} MINS</p>
              <button onClick={() => handleExerciseSelect(exercise)}>Select</button>
            </div>
          ))}
        </div>
      </div>

      <h2>Selected Exercises:</h2>
      <ul>
        {selectedExercises.map((exercise) => (
          <li key={exercise.id}>
            {exercise.title} - {exercise.kcal} KCAL - {exercise.mins} MINS
            <button onClick={() => handleExerciseRemove(exercise)}>Remove</button>
          </li>
        ))}
      </ul>

      <div className="summary">
        <h2>Workout Total:</h2>
        <p>{totalKcal} KCAL - {totalMins} MINS</p>
      </div>

      <div className="meal-selection">
        <h2>Select Meals:</h2>
        <div className="meal-list">
          {mealData.map((meal) => (
            <div key={meal.id} className="meal-card">
              <h3>{meal.title}</h3>
              <p>{meal.kcal} KCAL</p>
              <button onClick={() => handleMealSelect(meal)}>Select</button>
            </div>
          ))}
        </div>
      </div>

      <h2>Selected Meals:</h2>
      <ul>
        {selectedMeals.map((meal) => (
          <li key={meal.id}>
            {meal.title} - {meal.kcal} KCAL
            <button onClick={() => handleMealRemove(meal)}>Remove</button>
          </li>
        ))}
      </ul>

      <div className="summary">
        <h2>Meal Total:</h2>
        <p>{totalMealKcal} KCAL</p>
      </div>

      <button className="next-button" onClick={handleSchedule}>Schedule Workout</button>

      {showSchedule && (
        <div className="schedule-summary">
          <h2>Your Scheduled Workouts:</h2>
          {selectedExercises.map((exercise) => (
            <p key={exercise.id}>{exercise.title}</p>
          ))}
          <h2>Your Scheduled Meals:</h2>
          {selectedMeals.map((meal) => (
            <p key={meal.id}>{meal.title}</p>
          ))}
        </div>
      )}
    </div>
  );
};

export default ScheduleWorkout;
