import React, { useState } from 'react';
import './myplan.css'; 

const MyPlan = () => {
    // State for tracking selected workouts, total exercises, kcal burned, and total time
    const [workouts, setWorkouts] = useState([]);
    const [stats, setStats] = useState({ exercises: 0, kcal: 0, mins: 0 });

    // List of available workout plans
    const availableWorkouts = [
        { id: 1, title: 'FULL BODY WORKOUT', image: '/images/full-body.webp', kcal: 300, mins: 45 },
        { id: 2, title: 'ABS BEGINNER', image: '/images/abs-beginner.jpg', kcal: 150, mins: 20 },
        { id: 3, title: 'ARM BEGINNER', image: '/images/arm-beginner.webp', kcal: 100, mins: 15 }
    ];

    // Function to handle workout selection
    const handleWorkoutSelect = (workout) => {
        if (!workouts.includes(workout.id)) {
            setWorkouts([...workouts, workout.id]);
            setStats({
                exercises: stats.exercises + 1,
                kcal: stats.kcal + workout.kcal,
                mins: stats.mins + workout.mins
            });
        }
    };

    return (
      <div className="myplan-container">
        <div className="myplan-content">
          <h1>WORKOUT PLANS</h1>
          
          {/* Stats dynamically updated */}
          <div className="stats">
            <div>
              <h2>{stats.exercises}</h2>
              <p>EXERCISES</p>
            </div>
            <div>
              <h2>{stats.kcal}</h2>
              <p>KCAL</p>
            </div>
            <div>
              <h2>{stats.mins}</h2>
              <p>MINS</p>
            </div>
          </div>

          {/* Instructions */}
          <p className="choose-workout">Choose your Workout!</p>

          {/* Create Plan Button */}
          <button className="create-plan-btn">CREATE YOUR PLAN HERE!</button>
          
          {/* Workout Grid */}
          <div className="workout-grid">
            {availableWorkouts.map((workout) => (
              <div 
                key={workout.id} 
                className={`workout-card ${workouts.includes(workout.id) ? 'selected' : ''}`}
                onClick={() => handleWorkoutSelect(workout)}
              >
                <img src={workout.image} alt={workout.title} className="workout-image" />
                <p className="workout-title">{workout.title}</p>
              </div>
            ))}
          </div>
        </div>
      </div>
    );
};

export default MyPlan;
