// src/routes/WeightLoss.tsx
import React from 'react';
import './WeightLossSuggestions.css';
import beefitLogo from './images/beefit-logo.png';

const WeightLoss: React.FC = () => {
    return (
        <div className="weight-loss-suggestions">
            <img src={beefitLogo} alt="Beefit Logo" className="logo" />
            <h1>Weight Loss Suggestions</h1>
            <div className="suggestions-section">
                <h2>Suggestions for Your Journey:</h2>
                <ul>
                    <li>Increase daily activity levels.</li>
                    <li>Consider a balanced diet rich in whole foods.</li>
                    <li>Stay hydrated throughout the day.</li>
                    <li>Track your progress regularly.</li>
                    <li>Set realistic and achievable goals.</li>
                </ul>
            </div>
            <div className="meal-plan-section">
                <h2>Sample Meal Plan:</h2>
                <ul>
                    <li>Breakfast: Oatmeal with fruits.</li>
                    <li>Lunch: Grilled chicken salad.</li>
                    <li>Dinner: Baked salmon with vegetables.</li>
                </ul>
            </div>
            <button className="back-btn" onClick={() => navigate('/')}>Back to Home</button>
        </div>
    );
};

export default WeightLoss; // Ensure this line exists
