import React, { useState } from 'react';
import './myplan.css'; 

const MyPlan = () => {
    const [selectedWorkout, setSelectedWorkout] = useState(null);
    const [exercises, setExercises] = useState([]);
    const [totalKcal, setTotalKcal] = useState(0);
    const [totalMins, setTotalMins] = useState(0);

    const workoutPlans = {
        "FULL BODY WORKOUT": { exercises: ["Squats", "Push Ups", "Lunges"], kcal: 300, mins: 45 },
        "ABS BEGINNER": { exercises: ["Crunches", "Planks", "Leg Raises"], kcal: 200, mins: 30 },
        "ARM BEGINNER": { exercises: ["Bicep Curls", "Tricep Dips", "Push Ups"], kcal: 250, mins: 35 },
    };

    const handleWorkoutClick = (workout) => {
        setSelectedWorkout(workout);
        setExercises(workoutPlans[workout].exercises);
        setTotalKcal(workoutPlans[workout].kcal);
        setTotalMins(workoutPlans[workout].mins);
    };

    const handleEditRemove = (exercise) => {
        const updatedExercises = exercises.filter(ex => ex !== exercise);
        setExercises(updatedExercises);
        // Optionally, you can adjust kcal and mins accordingly
    };

    return (
        <div className="myplan-container">
            <div className="myplan-content">
                <h1>WORKOUT PLANS</h1>
                <div className="stats">
                    <div>
                        <h2>{exercises.length}</h2>
                        <p>EXERCISES</p>
                    </div>
                    <div>
                        <h2>{totalKcal}</h2>
                        <p>KCAL</p>
                    </div>
                    <div>
                        <h2>{totalMins}</h2>
                        <p>MINS</p>
                    </div>
                </div>
                <p className="choose-workout">Choose your Workout!</p>
                <button className="create-plan-btn">CREATE YOUR PLAN HERE!</button>
                <div className="workout-grid">
                    {Object.keys(workoutPlans).map(workout => (
                        <div className="workout-card" key={workout} onClick={() => handleWorkoutClick(workout)}>
                            <img src={`/images/${workout.toLowerCase().replace(/ /g, '-')}.webp`} alt={workout} className="workout-image" />
                            <p className="workout-title">{workout}</p>
                        </div>
                    ))}
                </div>
                {selectedWorkout && (
                    <div className="selected-workout">
                        <h2>{selectedWorkout}</h2>
                        <h3>Exercises:</h3>
                        <ul>
                            {exercises.map(exercise => (
                                <li key={exercise}>
                                    {exercise} 
                                    <button onClick={() => handleEditRemove(exercise)}>REMOVE</button>
                                </li>
                            ))}
                        </ul>
                    </div>
                )}
            </div>
        </div>
    );
};

export default MyPlan;
